extension.execute:
var apiURL = "https://okanneru.dotcloudapp.com/";

ajax ({
	url : apiURL,
	type : "get",
	timeout : 5000,
	success : function ( contents ) {
		log(contents);

		switch(contents) {
			case "月":
				runtimeValues.outputIndex = 0;
				break;
			case "火":
				runtimeValues.outputIndex = 1;
				break;
			case "水":
				runtimeValues.outputIndex = 2;
				break;
			case "木":
				runtimeValues.outputIndex = 3;
				break;
			case "金":
				runtimeValues.outputIndex = 4;
				break;
			case "土":
				runtimeValues.outputIndex = 5;
				break;
			case "日":
				runtimeValues.outputIndex = 6;
				break;
			default:
				runtimeValues.outputIndex = -1;
				break;
		}

		callbackSuccess( {
			resultType : "continue",
			runtimeValues : runtimeValues
		} );	
	},
	error : function ( request, errorMessage ) {
		log("Day : Network error");
		runtimeValues.outputIndex = -1;

		callbackSuccess( {
			resultType : "continue",
			runtimeValues : runtimeValues
		} );	
	}
});

return {
	resultType : "pause"
};
